import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import FilterBar from './FilterBar'
import TaskContainer from './TaskContainer'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    selectedCategory: 'All',
    filtered: ''
  }

  changeSelectedCategory = (category) => {
    this.setState({selectedCategory: category})
  }

  render() {
    (this.state.selectedCategory === 'All') ? this.setState({filtered: this.state.tasks}) : this.setState({filtered: this.state.tasks.filter(task => task.category === this.state.selectedCategory)})
    return (
      <div className="App">
        <h2>My tasks</h2>
        <FilterBar changeSelectedCategory={this.changeSelectedCategory} categories={CATEGORIES} selectedCategory={this.state.selectedCategory}/>
        <TaskContainer tasks={this.state.filtered}/> 
      </div>
    );
  }
}


export default App;
